import React from "react";
import { Link } from "react-router-dom";
import AuthStatus from "./AuthStatus";
import { useAuth } from "../context/AuthContext"; // Assurez-vous d'avoir un contexte d'authentification

function Home() {
  const { user } = useAuth(); // Récupération des infos de l'utilisateur

  return (
    <div>
      <h1>Bienvenue sur le Pokédex</h1>
      <AuthStatus />
      {user ? (
        <>
          <p>Bienvenue, {user.username} !</p>
          <Link to="/pokemon-list">Gérer mes Pokémon</Link>
        </>
      ) : (
        <>
          <p>Connecte-toi pour gérer tes Pokémon !</p>
          <Link to="/login">Se connecter</Link> | <Link to="/register">S'inscrire</Link>
        </>
      )}
    </div>
  );
}

export default Home;
