import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const EditTrainer = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [trainer, setTrainer] = useState({
    username: "",
    caughtPokemonCount: 0,
    favoritePokemon: "",
    team: [],
  });

  useEffect(() => {
    const fetchTrainer = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(`http://localhost:5000/api/trainers/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTrainer(response.data);
      } catch (error) {
        console.error("Erreur lors de la récupération du dresseur :", error);
      }
    };
    fetchTrainer();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTrainer({ ...trainer, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await axios.put(`http://localhost:5000/api/trainers/${id}`, trainer, {
        headers: { Authorization: `Bearer ${token}` },
      });
      navigate(`/trainer/${id}`); // Redirection vers la page du dresseur
    } catch (error) {
      console.error("Erreur lors de la mise à jour du dresseur :", error);
    }
  };

  return (
    <div>
      <h2>Modifier le dresseur</h2>
      <form onSubmit={handleSubmit}>
        <label>Nom d'utilisateur :</label>
        <input type="text" name="username" value={trainer.username} onChange={handleChange} required />

        <label>Pokémons capturés :</label>
        <input type="number" name="caughtPokemonCount" value={trainer.caughtPokemonCount} onChange={handleChange} required />

        <label>Pokémon favori :</label>
        <input type="text" name="favoritePokemon" value={trainer.favoritePokemon} onChange={handleChange} />

        <button type="submit">Enregistrer</button>
      </form>
    </div>
  );
};

export default EditTrainer;
