import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const TrainerEdit = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  
  const [trainer, setTrainer] = useState(null);
  const [pokemons, setPokemons] = useState([]);
  const [selectedTeam, setSelectedTeam] = useState([]);
  const [imageFile, setImageFile] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");

        // Récupérer le dresseur
        const trainerRes = await axios.get(`http://localhost:5000/api/trainers/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        setTrainer(trainerRes.data);
        setSelectedTeam(trainerRes.data.team.map(p => p._id));

        // Récupérer les Pokémon disponibles
        const pokemonsRes = await axios.get("http://localhost:5000/api/pkmn", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setPokemons(pokemonsRes.data);
      } catch (error) {
        console.error("❌ Erreur lors de la récupération des données :", error);
      }
    };

    fetchData();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    const formData = new FormData();
    formData.append("team", selectedTeam);
    if (imageFile) formData.append("image", imageFile);

    try {
      await axios.put(`http://localhost:5000/api/trainers/${id}`, formData, {
        headers: { Authorization: `Bearer ${token}`, "Content-Type": "multipart/form-data" },
      });

      navigate("/trainers");
    } catch (error) {
      console.error("❌ Erreur lors de la modification du dresseur :", error);
    }
  };

  if (!trainer) return <p>Chargement...</p>;

  return (
    <div>
      <h1>Modifier {trainer.username}</h1>
      <form onSubmit={handleSubmit}>
        <label>Image de profil :</label>
        <input type="file" onChange={(e) => setImageFile(e.target.files[0])} />
        <br />
        <label>Équipe Pokémon :</label>
        {selectedTeam.map((pokeId, index) => (
          <select key={index} onChange={(e) => {
            const newTeam = [...selectedTeam];
            newTeam[index] = e.target.value;
            setSelectedTeam(newTeam);
          }} value={pokeId}>
            <option value="">Choisir un Pokémon</option>
            {pokemons.map((pokemon) => (
              <option key={pokemon._id} value={pokemon._id}>
                {pokemon.name}
              </option>
            ))}
          </select>
        ))}
        <br />
        <button type="submit">Modifier</button>
      </form>
    </div>
  );
};

export default TrainerEdit;
