import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../../context/AuthContext";

const TrainerManagement = () => {
  const { user } = useAuth();
  const [trainers, setTrainers] = useState([]);
  const [newTrainer, setNewTrainer] = useState({ name: "", level: 1 });

  useEffect(() => {
    fetchTrainers();
  }, []);

  const fetchTrainers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/trainers");
      setTrainers(response.data);
    } catch (error) {
      console.error("Erreur lors de la récupération des trainers:", error);
    }
  };

  const handleCreateTrainer = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/trainers", newTrainer);
      setTrainers([...trainers, response.data]); // Mettre à jour la liste
      setNewTrainer({ name: "", level: 1 }); // Réinitialiser le formulaire
    } catch (error) {
      console.error("Erreur lors de la création du trainer:", error);
    }
  };

  return (
    <div>
      <h2>Gestion du Trainer</h2>

      {user ? (
        <>
          <h3>Ajouter un Trainer</h3>
          <input
            type="text"
            placeholder="Nom du Trainer"
            value={newTrainer.name}
            onChange={(e) => setNewTrainer({ ...newTrainer, name: e.target.value })}
          />
          <button onClick={handleCreateTrainer}>Créer</button>

          <h3>Liste des Trainers</h3>
          <ul>
            {trainers.map((trainer) => (
              <li key={trainer.id}>{trainer.name} (Niveau {trainer.level})</li>
            ))}
          </ul>
        </>
      ) : (
        <p>Veuillez vous connecter pour gérer votre trainer.</p>
      )}
    </div>
  );
};

export default TrainerManagement;
