import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const TrainerDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [trainer, setTrainer] = useState(null);

  useEffect(() => {
    const fetchTrainer = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(`http://localhost:5000/api/trainers/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTrainer(response.data);
      } catch (error) {
        console.error("Erreur lors de la r√©cup√©ration du dresseur :", error);
      }
    };
    fetchTrainer();
  }, [id]);

  if (!trainer) return <p>Chargement...</p>;

  return (
    <div>
      <h1>{trainer.username}</h1>
      <p><strong>Pok√©mons captur√©s:</strong> {trainer.caughtPokemonCount}</p>
      <p><strong>Pok√©mon favori:</strong> {trainer.favoritePokemon ? trainer.favoritePokemon.name : "Aucun"}</p>
      <p><strong>√âquipe:</strong> {trainer.team.length > 0 ? trainer.team.map(pkmn => pkmn.name).join(", ") : "Aucune √©quipe"}</p>

      {/* üîπ Bouton Modifier qui redirige vers la page d'√©dition */}
      <button onClick={() => navigate(`/trainer/edit/${id}`)}>Modifier</button>
    </div>
  );
};

export default TrainerDetail;
