import React, { useState, useEffect } from "react";
import api from "../../utils/axiosConfig"; // ‚úÖ Utilisation d'Axios pr√©-configur√©
import { useAuth } from "../../context/AuthContext";

const TrainerManagement = () => {
  const { user } = useAuth();
  const [trainers, setTrainers] = useState([]);
  const [trainer, setTrainer] = useState(null);
  const [favoritePokemon, setFavoritePokemon] = useState("");

  useEffect(() => {
    fetchTrainers();
    if (user?.token) {
      fetchTrainerProfile();
    } else {
      console.warn("‚ö†Ô∏è Aucun token trouv√©, l'utilisateur n'est pas connect√©.");
    }
  }, [user]);

  const fetchTrainers = async () => {
    try {
      const response = await api.get("/trainers");
      setTrainers(response.data);
    } catch (error) {
      console.error("‚ùå Erreur de r√©cup√©ration des trainers :", error.response?.data || error.message);
    }
  };

  const fetchTrainerProfile = async () => {
    if (!user?.token) {
      console.warn("üö´ Tentative de r√©cup√©ration du profil sans token !");
      return;
    }

    try {
      const response = await api.get("/trainers/me");
      setTrainer(response.data);
      console.log("‚úÖ Profil Trainer r√©cup√©r√© :", response.data);
    } catch (error) {
      console.error("‚ùå Erreur de r√©cup√©ration du profil :", error.response?.data || error.message);
    }
  };

  const setFavorite = async (pokemonId) => {
    if (!user?.token) {
      console.warn("üö´ Impossible de d√©finir un favori sans token !");
      return;
    }

    try {
      await api.post("/trainers/favorite", { pokemonId });
      fetchTrainerProfile(); // ‚úÖ Rafra√Æchir les donn√©es apr√®s la mise √† jour
    } catch (error) {
      console.error("‚ùå Erreur de mise √† jour du favori :", error.response?.data || error.message);
    }
  };

  return (
    <div>
      <h2>Gestion du Trainer</h2>

      {trainer ? (
        <div>
          <h3>Bienvenue, {trainer.username} !</h3>
          <p>Pok√©mon attrap√©s : {trainer.caughtPokemonCount}</p>

          <h4>Pok√©mon Favori :</h4>
          {trainer.favoritePokemon ? (
            <p>{trainer.favoritePokemon.name}</p>
          ) : (
            <p>Aucun favori choisi.</p>
          )}

          <h4>Changer le favori :</h4>
          {trainer.caughtPokemons.length > 0 ? (
            trainer.caughtPokemons.map((pokemon) => (
              <button key={pokemon._id} onClick={() => setFavorite(pokemon._id)}>
                {pokemon.name}
              </button>
            ))
          ) : (
            <p>Aucun Pok√©mon attrap√©.</p>
          )}

          <h4>√âquipe Pok√©mon :</h4>
          {trainer.team.length > 0 ? (
            <ul>
              {trainer.team.map((pokemon) => (
                <li key={pokemon._id}>{pokemon.name}</li>
              ))}
            </ul>
          ) : (
            <p>Aucune √©quipe Pok√©mon d√©finie.</p>
          )}
        </div>
      ) : (
        <p>Chargement des donn√©es du Trainer...</p>
      )}
    </div>
  );
};

export default TrainerManagement;
