import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../../context/AuthContext";

const TrainerManagement = () => {
  const { user } = useAuth();
  const [trainers, setTrainers] = useState([]);
  const [trainer, setTrainer] = useState(null);
  const [favoritePokemon, setFavoritePokemon] = useState("");

  useEffect(() => {
    fetchTrainers();
    if (user) fetchTrainerProfile();
  }, [user]);

  const fetchTrainers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/trainers");
      setTrainers(response.data);
    } catch (error) {
      console.error("Erreur de récupération des trainers :", error);
    }
  };

  const fetchTrainerProfile = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/trainers/me", {
        headers: { Authorization: `Bearer ${user.token}` },
      });
      setTrainer(response.data);
    } catch (error) {
      console.error("Erreur de récupération du profil :", error);
    }
  };

  const setFavorite = async (pokemonId) => {
    try {
      await axios.post(
        "http://localhost:5000/api/trainers/favorite",
        { pokemonId },
        { headers: { Authorization: `Bearer ${user.token}` } }
      );
      fetchTrainerProfile();
    } catch (error) {
      console.error("Erreur de mise à jour du favori :", error);
    }
  };

  return (
    <div>
      <h2>Gestion du Trainer</h2>

      {trainer && (
        <div>
          <h3>Bienvenue, {trainer.username} !</h3>
          <p>Pokémon attrapés : {trainer.caughtPokemonCount}</p>

          <h4>Pokémon Favori :</h4>
          {trainer.favoritePokemon ? (
            <p>{trainer.favoritePokemon.name}</p>
          ) : (
            <p>Aucun favori choisi.</p>
          )}

          <h4>Changer le favori :</h4>
          {trainer.caughtPokemons.map((pokemon) => (
            <button key={pokemon._id} onClick={() => setFavorite(pokemon._id)}>
              {pokemon.name}
            </button>
          ))}

          <h4>Équipe Pokémon :</h4>
          <ul>
            {trainer.team.map((pokemon) => (
              <li key={pokemon._id}>{pokemon.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default TrainerManagement;
